/**
	* @brief Function to write to LED with pushbutton info
	* @param none
	* @retval none
*/
void WriteLED(void){
		int buttonState = HAL_GPIO_ReadPin(GPIOC,GPIO_PIN_13);
		
		if( buttonState == 0){ //active low
				HAL_GPIO_WritePin(GPIOB, GPIO_PIN_14, GPIO_PIN_SET);
		} else {
				HAL_GPIO_WritePin(GPIOB, GPIO_PIN_14, GPIO_PIN_RESET);
		}
	
}

/**
	* @brief Function to write array to flash memory
	* @param array
	* @param arrayLength
	* @retval none
*/
void TBWriteFlash(){
	uint64_t Data;
	uint32_t TypeProgram;
	HAL_FLASH_Unlock();
	HAL_FLASH_Program_IT(TypeProgram,voiceSignal[999],Data);
	HAL_FLASH_IRQHandler();
	HAL_FLASH_Lock();
	
}
